mapper.update:

update stock_theme_static
set `date` = #{date,jdbcType=VARCHAR},
  theme = #{theme,jdbcType=VARCHAR},
  object_static_Array = #{objectStaticArray,jdbcType=LONGVARCHAR},
  object_sign = #{objectSign,jdbcType=VARCHAR}
where id = #{id,jdbcType=VARCHAR}

mapper.delete:
delete from stock_theme_static
where `date`=#{date,jdbcType=VARCHAR} and object_sign=#{objectSign,jdbcType=VARCHAR} and
theme=#{theme,jdbcType=VARCHAR}

mapper.select:
select

from stock_theme_static

  theme=#{theme,jdbcType=VARCHAR} and object_sign=#{objectSign,jdbcType=VARCHAR}

    and `date` >= #{minDate,jdbcType=VARCHAR}


    and `date` <= #{maxDate,jdbcType=VARCHAR}


order by date  asc
